public_bridge() {
	addcode up 2 5 "brctl addbr $if"
	for i; do
		addcode up 3 5 "ip link set $i up"
		addcode up 3 6 "brctl addif $if $i"
	done

	# interfaces are implicitly removed from the bridge on delbr
	addcode down 1 1 "brctl delbr $if"
}
